import { createTheme } from '@material-ui/core/styles';
import { red, green, yellow } from '@material-ui/core/colors';

// Create a theme instance.
export const theme = createTheme({
  palette: {
    type: 'light',
    primary: {
      main: '#42A5f6',
    },
    secondary: {
      main: '#000000',
    },
    success: {
      main: green.A400,
    },
    warning: {
      main: yellow.A400,
    },
    error: {
      main: red.A400,
    },
    border: {
      main: '#D5D5D5',
      light: '#F1F1F1',
    },
  },
});
