import React, { useRef, useEffect, useState } from "react";
import bodyPose from "./components/features/bodyPoseDetection";
import FaceLandmarkDetection from "./components/features/faceLandmarkDetection";
import HandShapeDetection from "./components/features/handShapeDetection";
import SelfieSegmentationDetection from "./components/features/selfieSegmentation";
import FaceShapeDetection from "./components/features/faceDetection";
import Webcam from "react-webcam";
import "./App.css";
import IconButton from "@material-ui/core/IconButton";
import { Box, Grid, makeStyles } from "@material-ui/core";
import DeleteIcon from "@material-ui/icons/Delete";
import AlarmIcon from "@material-ui/icons/Alarm";
import { mergeClasses } from "@material-ui/styles";
import solve from "./components/features/bodyPoseDetection";
import BodyPoseDetection from "./components/features/bodyPoseDetection";
import imageCompression from "browser-image-compression";

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundImage: "url()",
    backgroundPosition: "center",
    minHeight: "100vh",
    width: "100wh",
    background: "#0002",
    // display: 'flex',
    // flexDirection: 'column',
    // alignItems: 'center',
    // justifyContent: 'center',
    // fontSize: 'calc(10px + 2vmin)',
    // color: 'white'
  },
  buttonGrp: {
    position: "absolute",
    bottom: 50,
    zIndex: 20,
  },
}));

function App() {
  const classes = useStyles();
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [URL , setURL] = useState(null);

  useEffect(() => {
    bodyPose(webcamRef, canvasRef);
  }, []);

  const toBase64 = (file) =>
  new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setURL(imageSrc);
    // this.compressPhoto(imageSrc);
  };

  const compressPhoto = (pic) => {
    //https://www.npmjs.com/package/browser-image-compression
    var options = {
      maxSizeMB: 0.5,
      // maxWidthOrHeight: 200,
      useWebWorker: true
      // fileType: "image/png"
    };

    imageCompression
      .getFilefromDataUrl(pic)
      .then((file) => imageCompression(file, options))
      .then(toBase64)
      .then((newZipPic) => {
        // console.log(pic);
        this.setState({ url: pic, NewZipPic: newZipPic }, () =>
          this.savePhoto()
        );
      })
      .catch(function (error) {
        console.log(error.message);
      });
  };


  return (
    <div className="App">
      <Box>
        <center>
          <div className="App">
            <Webcam
              ref={webcamRef}
              style={{
                position: "absolute",
                marginLeft: "auto",
                marginRight: "auto",
                left: 0,
                right: 0,
                textAlign: "center",
                zindex: 9,
                width: 640,
                height: 480,
              }}
            />{" "}
            <canvas
              ref={canvasRef}
              className="output_canvas"
              style={{
                position: "absolute",
                marginLeft: "auto",
                marginRight: "auto",
                left: 0,
                right: 0,
                textAlign: "center",
                zindex: 9,
                width: 640,
                height: 480,
              }}
            ></canvas>
          </div>
        </center>

        {/* <BodyPoseDetection /> */}
        {/* <FaceLandmarkDetection /> */}
        {/* <SelfieSegmentationDetection /> */}
        {/* <HandShapeDetection /> */}
        {/* <FaceShapeDetection /> */}
      </Box>
      <Box className={classes.buttonGrp}>
        <Box display="flex">
          <IconButton aria-label="delete" style={{ background: "#0002" }}>
            <DeleteIcon />
          </IconButton>
          <IconButton aria-label="delete" color="primary">
            <DeleteIcon />
          </IconButton>
          <IconButton color="secondary" aria-label="add an alarm" onClick={capturePhoto}>
            <AlarmIcon />
          </IconButton>
        </Box>
      </Box>
      {URL &&  <img
              style={{ height: "100px", width: "100px" }}
              src={URL} //show pic in state
              alt="Screenshot"
              //onClick={() => this.selectPic(x)} // click pic to select that pic, and remove others
            />}

    </div>
  );
}

export default App;
