import React, { useRef, useEffect, useState } from "react";

import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Backdrop from '@material-ui/core/Backdrop';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import CircularProgress from '@material-ui/core/CircularProgress';
import InputAdornment from '@material-ui/core/InputAdornment';
import PersonOutlineOutlinedIcon from '@material-ui/icons/PersonOutlineOutlined';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import IconButton from '@material-ui/core/IconButton';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import Hidden from '@material-ui/core/Hidden';
import SignIn from "../components/Auth/signIn";
import SignUp from "../components/Auth/signUp";
import Image from "../media/home.jpg"

const useStyles = makeStyles((theme) => ({
    root:  {
        // background : "#5d4037",
        backgroundImage: `url(${Image})`,
        backgroundPosition: 'center', 
        backgroundSize: 'cover', 
        backgroundRepeat: 'no-repeat',
    },
    content: {
      padding: '2em 0em',
      minHeight: '100vh',
    },
    backdrop: {
      zIndex: 99,
      color: '#fff',
    },
    title: {
      fontWeight: 900,
      lineHeight: 1.15,
    },
    titleSignIn: {
      fontWeight: 900,
      lineHeight: 1.15,
    },
    titleHyperlink: {
      color: '#0D47A1',
      textDecoration: 'none',
    },
    titleDescription: {
      fontSize: '1.5rem',
    },
    titleBox: {
      backgroundColor: '#42A5f6',
      height: 180,
      padding: '2.5em 1.5em',
      display: 'flex',
      alignItems: 'center',
    },
    subHeading: {
      fontSize: 13,
      color: '#9E9E9E',
    },
    contentBox: {
      width: 420,
      backgroundColor: '#42A5f6',
      padding: '2.5em',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      borderTopLeftRadius: 4,
      borderBottomLeftRadius: 4,
    },
    displayNone: {
      display: 'none !important',
    },
    loginBox: {
      width: 350,
      height: 550,
      backgroundColor: '#FFFFFF',
      borderBottom: '1px solid #E0E0E0',
      borderRight: '1px solid #E0E0E0',
      borderUp : '1px solid #E0E0E0',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      borderTopRightRadius: 4,
      borderBottomRightRadius: 4,
      [theme.breakpoints.down('sm')]: {
        border: '1px solid #E0E0E0',
        height: 600,
        justifyContent: 'space-between',
        borderRadius: 4,
      },
    },
    form: {
      padding: '1em 1.5em',
    },
    grid: {
      width: '100%',
      marginTop: 45,
      marginBottom: 40,
    },
    inputGridItem: {
      width: '100%',
    },
    input: {
      color: '#424242',
      height: 45,
      border: 0,
      backgroundColor: '#F5F5F5',
      borderRadius: 4,
      padding: '1em',
      '&:hover': {
        backgroundColor: '#EEEEEE',
      },
    },
    errorText: {
      marginTop: 10,
      fontSize: 13,
    },
    resendEmailLink: {
      color: theme.palette.primary.main,
      textDecoration: 'none',
      fontSize: 14,
      '&:hover': {
        userSelect: 'none',
        cursor: 'pointer',
      },
    },
    loginButton: {
      marginTop: 25,
      height: 45,
      color: 'white',
      width: '100%',
      borderRadius: 4,
    },
    helpGrid: {
      marginTop: 16,
      textAlign: 'center',
    },
    footer: {
      height: 40,
      width: '100%',
      marginBottom: 10,
      padding: '15 5',
    },
    footerGrid: {
      width: '100%',
      height: '100%',
    },
    footerIndiaText: {
      fontSize: 12,
    },




    semiCircle1: {
      position: 'absolute',
      zIndex: -2,
      top: -80,
      right: 0,
      transform: 'translate(0%, -30%)',
      width: 400,
      height: 400,
      borderRadius: 200,
      backgroundColor: '#BBDEFB',
      opacity: 0.5,
    },
    semiCircle2: {
      position: 'absolute',
      zIndex: -2,
      bootom: 0,
      left: -50,
      transform: 'translate(-50%, 60%)',
      width: 400,
      height: 400,
      borderRadius: 200,
      backgroundColor: '#BBDEFB',
      opacity: 0.5,
    },
    semiCircle3: {
      position: 'absolute',
      zIndex: 0,
      transform: 'translate(25%, 0%)',
      width: 400,
      height: 550,
      borderRadius: '400px 0 0 400px',
      backgroundColor: '#E1F5FE',
      opacity: 0.1,
    },
    semiCircle4: {
      position: 'absolute',
      zIndex: 0,
      transform: 'translate(0%, 0%)',
      width: 400,
      height: 550,
      borderRadius: '0 400px 400px 0',
      backgroundColor: '#1E88E5',
      opacity: 0.1,
    },
  }));


export default function Home() {
    const classes = useStyles();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [loginLoading, setLoginLoading] = useState(false);
    const [emailError, setEmailError] = useState(false);
    const [passwordError, setPasswordError] = useState(false);
    const [showSignIn , setShowSignIn] = useState(true);

    const handleClickShowSignIn = () => {
      setShowSignIn(true);
    }

    const handleClickShowSignUp = () => {
      setShowSignIn(false);
    }
    const handleClickShowPassword = () => setShowPassword(!showPassword);

    return(
        <div className={classes.root}>
        <main>
          <Backdrop open={loginLoading} className={classes.backdrop}>
            <CircularProgress color="inherit" />
          </Backdrop>

          <Grid
            container
            direction="column"
            justifyContent="center"
            alignItems="center"
            className={classes.content}
          >
            <Grid
              container
              item
              direction="column"
              justifyContent="center"
              alignItems="center"
              xs={12}
              spacing={3}
            >
              <Box display="flex">
                <Hidden smDown>
                  <Box className={classes.semiCircle3}></Box>
                  <Box className={classes.semiCircle4}></Box>

                  <Box className={classes.contentBox}>
                    <Box
                      display="flex"
                      alignItems="flex-start"
                      style={{ zIndex: 5 }}
                    >
                      <Grid item>
                       <Typography variant="h4"
                          align="left"
                          className={classes.title}>FaceKart</Typography>
                      </Grid>
                    </Box>
                    <Box style={{ zIndex: 5 }}>
                      <Grid item>
                        <Typography
                          variant="h3"
                          align="left"
                          className={classes.title}
                          style={{ color: '#FFF' }}
                        >
                          Welcome to<br></br>
                            UMT
                          {' '}
                          Console
                        </Typography>
                      </Grid>
                    </Box>
                    <Box
                      style={{
                        border: '1 solid #FFF',
                        zIndex: 5,
                      }}
                    ></Box>
                  </Box>
                </Hidden>
                <Box className={classes.loginBox}>
                  <Hidden mdUp>
                    <Box className={classes.titleBox}>
                      <Grid item>
                        <Typography
                          variant="h4"
                          align="left"
                          className={classes.title}
                          style={{ color: '#FFF', fontSize: 40 }}
                        >
                          Welcome to<br></br>
                          <a
                            href="https://urbanmatrix.co.in"
                            className={classes.titleHyperlink}
                          >
                            UMT
                          </a>{' '}
                          Console
                        </Typography>
                      </Grid>
                    </Box>
                  </Hidden>
                  {
                    showSignIn ?  <SignIn handleClickShowSignUp={handleClickShowSignUp} /> : <SignUp handleClickShowSignIn={handleClickShowSignIn}  />
                  }

                  {/* <Box className={classes.form}>
                    <Hidden smDown>
                      <Box mb={4}>
                        <Typography
                          variant="h4"
                          align="left"
                          className={classes.titleSignIn}
                        >
                          Sign In
                        </Typography>
                      </Box>
                    </Hidden>
                    <Grid item className={classes.inputGridItem}>
                      <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="email"
                        name="email"
                        placeholder="Email"
                        InputProps={{
                          startAdornment: (
                            <InputAdornment position="start">
                              <PersonOutlineOutlinedIcon
                                style={{
                                  color: '#42A5f6',
                                  fontSize: 20,
                                  marginRight: 5,
                                }}
                              />
                            </InputAdornment>
                          ),
                          className: classes.input,
                          disableUnderline: true,
                        }}
                        helperText={emailError ? 'Invalid email address' : ''}
                        autoFocus={true}
                        error={emailError}
                        value={email}

                      />
                    </Grid>
                    <Grid item className={classes.inputGridItem}>
                      <TextField
                        margin="normal"
                        required
                        fullWidth
                        name="password"
                        type={showPassword ? 'text' : 'password'}
                        id="password"
                        placeholder="Password"
                        helperText={
                          passwordError
                            ? 'Password should be greater than 7 characters'
                            : ''
                        }
                        InputProps={{
                          startAdornment: (
                            <InputAdornment position="start">
                              <LockOutlinedIcon
                                style={{
                                  color: '#42A5f6',
                                  fontSize: 20,
