import React, { useRef, useEffect, useState } from "react";

import Webcam from "react-webcam";
import "../App.css";
import IconButton from "@material-ui/core/IconButton";
import { Box, Button, makeStyles, Typography } from "@material-ui/core";
import Image from "../media/home.jpg";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import { useNavigate } from "react-router-dom";

// import xxx from "./components/features/handShapeDetection";
// import yyy from "./components/features/selfieSegmentation";
// import zzz from "./components/features/bodyPoseDetection";
import aaa from "../components/features/faceLandmarkDetection";
// import ccc from "./components/features/objectronDetection";
// import ddd from "./components/features/faceDetection";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import faceMask from "../components/features/faceLandmarkDetection";

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundImage: `url(${Image})`,
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh",
    width: "100wh",
  },
  webcam: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginTop: 80,
  },
  buttonGrp: {
    position: "absolute",
    bottom: 50,
    zIndex: 20,
  },
  sideButton: {
    bottom: 270,
    //widht : 200,
    // height: Screen ?  520 : window.innerHeight ,
    // background : '#0002',
    zIndex: 20,
  },
  iconButton: {
    //padding: 0,
    borderRadius: 0,
    '&:hover': {
      color: '#000',
    },
    fontSize: 12,
    color: '#FFF',
    border: '1px solid #fff',
  },
}));

export default function Feature() {
  const classes = useStyles();
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [value, setValue] = useState(0);
  //const [URL, setURL] = useState(null);
  const Screen = useMediaQuery("(min-width:600px)");
  const navigate = useNavigate();

  const leftChange = () => {
    if (value > 0) {
      setValue(value - 1);
    }
  };

  const rightChange = () => {
    if (value < 3) {
      setValue(value + 1);
    }
  };

  useEffect(() => {
    // aaa(webcamRef, canvasRef);
    if (value === 0) {
      faceMask(webcamRef, canvasRef);
    }
    //  else if (value === 1) {
    //   yyy(webcamRef, canvasRef);
    // } else if (value === 2) {
    //   zzz(webcamRef, canvasRef);
    // } else if (value === 3) {
    //   aaa(webcamRef, canvasRef);
    // }
  }, [value]);

  // const capturePhoto = () => {
  //   const imageSrc = webcamRef.current.getScreenshot();
  //   setURL(imageSrc);
  // };

  const signOut = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  useEffect(() => {
    if (!localStorage.getItem("token")) {
      navigate("/");
    }
  }, []);

  return (
    <div className={classes.root}>
      <main>
        <Box disply="flex" justifyContent="space-between">
          <Typography
            variant="subtitle2"
            color="textSecondary"
            align="center"
            style={{
              userSelect: "none",
              fontSize: 20,
              letterSpacing: 6,
              marginTop: 0,
              position: "absolute",
              left: 25,
              top: 20,
              color : '#FFF'
            }}
          >
            FACEKART
          </Typography>
          <Button
            style={{
              color: "#FFF",
              fontSize: 20,
              position: "absolute",
              right: 10,
              top: 10,
            }}
            variant="outlined"
            className={classes.iconButton}
            onClick={signOut}
          >
            SignOut
          </Button>
        </Box>

        <div className={classes.webcam}>
          <Webcam
            ref={webcamRef}
            style={{
              position: "absolute",
              marginLeft: "auto",
              marginRight: "auto",
              left: 0,
              right: 0,
              textAlign: "center",
              zindex: 9,
              width: Screen ? 720 : 340,
              height: Screen ? 520 : 380,
            }}
          />{" "}
          <canvas
            ref={canvasRef}
            className="output_canvas"
            style={{
              position: "absolute",
              marginLeft: "auto",
              marginRight: "auto",
              left: 0,
              right: 0,
              textAlign: "center",
              zindex: 9,
              width: Screen ? 720 : 340,
              height: Screen ? 520 : 380,
            }}
          ></canvas>
        </div>
        <Box
          display="flex"
          justifyContent="space-between"
          style={{ width: Screen ? 720 : 340 }}
          className={classes.sideButton}
        >
          <IconButton
            style={{
              color: "#FFF",
              fontSize: 20,
              padding: 20,
              background: "#0004",
            }}
            onClick={leftChange}
          >
            <ArrowBackIosIcon />
          </IconButton>
          <IconButton
            style={{
              color: "#FFF",
              fontSize: 20,
              padding: 20,
              background: "#0004",
            }}
            onClick={rightChange}
          >
            <ArrowForwardIosIcon />
          </IconButton>
        </Box>
        <Box className={classes.buttonGrp}>
          {/* 
          {URL && (
            <img
              style={{ height: "100px", width: "100px" }}
              src={URL} //show pic in state
              alt="Screenshot"
              //onClick={() => this.selectPic(x)} // click pic to select that pic, and remove others
            />
          )} */}
        </Box>
      </main>
    </div>
  );
}
