import React, { useRef, useEffect, useState } from "react";

import Webcam from "react-webcam";
import "../App.css";
import IconButton from "@material-ui/core/IconButton";
import { Box, Grid, makeStyles } from "@material-ui/core";
import DeleteIcon from "@material-ui/icons/Delete";
import AlarmIcon from "@material-ui/icons/Alarm";
import { mergeClasses } from "@material-ui/styles";
import Image from "../media/home.jpg";
import useMediaQuery from '@material-ui/core/useMediaQuery';

// import xxx from "./components/features/handShapeDetection";
// import yyy from "./components/features/selfieSegmentation";
// import zzz from "./components/features/bodyPoseDetection";
import aaa from "../components/features/faceLandmarkDetection";
// import ccc from "./components/features/objectronDetection";
// import ddd from "./components/features/faceDetection";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundImage: `url(${Image})`,
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height : '100vh',
    width : '100wh'
  },
  webcam : {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop : 80,
  },
  buttonGrp: {
    position: "absolute",
    bottom: 50,
    zIndex: 20,
  },
  sideButton: {
    bottom: 270,
    width: 320,
    // background : '#0002',
    zIndex: 20,
  },
}));

export default function Feature() {
  const classes = useStyles();
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [value, setValue] = useState(0);
  const [URL, setURL] = useState(null);
  const Screen = useMediaQuery('(min-width:600px)');

  const leftChange = () => {
    if (value > 0) {
      setValue(value - 1);
    }
  };

  const rightChange = () => {
    if (value < 3) {
      setValue(value + 1);
    }
  };

  useEffect(() => {
    aaa(webcamRef, canvasRef);
    // if (value === 0) {
    //   xxx(webcamRef, canvasRef);
    // } else if (value === 1) {
    //   yyy(webcamRef, canvasRef);
    // } else if (value === 2) {
    //   zzz(webcamRef, canvasRef);
    // } else if (value === 3) {
    //   aaa(webcamRef, canvasRef);
    // }
    //xxx(webcamRef, canvasRef);
  }, [value]);

  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setURL(imageSrc);
  };

  return (
    <div className={classes.root}>
       <main>
          <div className={classes.webcam}>
            <Webcam
              ref={webcamRef}
              style={{
                position: "absolute",
                marginLeft: "auto",
                marginRight: "auto",
                left: 0,
                right: 0,
                textAlign: "center",
                zindex: 9,
                width: Screen ? 720  :window.innerWidth,
                height: Screen ?  520 : window.innerHeight ,
              }}
            />{" "}
            <canvas
              ref={canvasRef}
              className="output_canvas"
              style={{
                position: "absolute",
                marginLeft: "auto",
                marginRight: "auto",
                left: 0,
                right: 0,
                textAlign: "center",
                zindex: 9,
                width: Screen ? 720  :window.innerWidth,
                height: Screen ?  520 : window.innerHeight ,
              }}
            ></canvas>
          </div>
        <Box
          display="flex"
          justifyContent="space-between"
          className={classes.sideButton}
        >
          <IconButton
            style={{
              color: "#FFF",
              fontSize: 20,
              padding: 20,
              background: "#0004",
            }}
            onClick={leftChange}
          >
            <ArrowBackIosIcon />
          </IconButton>
          <IconButton
            style={{
              color: "#FFF",
              fontSize: 20,
              padding: 20,
              background: "#0004",
            }}
            onClick={rightChange}
          >
            <ArrowForwardIosIcon />
          </IconButton>
        </Box>
        {/* <Box className={classes.buttonGrp}>
          <IconButton
            style={{ color: "#FFF", fontSize: 20 }}
            onClick={capturePhoto}
          >
            <ArrowForwardIosIcon />
          </IconButton>

          {URL && (
            <img
              style={{ height: "100px", width: "100px" }}
              src={URL} //show pic in state
              alt="Screenshot"
              //onClick={() => this.selectPic(x)} // click pic to select that pic, and remove others
            />
          )}
        </Box> */}
      </main>
    </div>
  );
}
